'''plot the energy dump file created by Monte Carlo Engine''' import pandas as pd import numpy as np import matplotlib.pyplot as plt with open("energydump.txt", 'r') as f:    for line in f:        if line.startswith("Energy"):            break    df = pd.read_csv(f , sep='|', names =["Energy", "Step_Number"] )    f.close()     energy = df["Energy"].valuesstep_num = df["Step_Number"].values plt.scatter(step_num, energy, s=8)plt.ylabel('Energy of system')plt.xlabel('Number of steps performed')